import styled from "styled-components";
import { Link } from "react-router-dom";
const Wrapper = styled.div`
width: 100%;
background: #fff;
`;

const Main1 = styled.div`
position: sticky;
top: 0;
height: 80px;
background-color: white;
box-shadow: 0 .1px 5px #ccc;

nav{
  display: flex;
  max-width: 1158px;
  height: 80px;

  background-color: white;
  top: 0;
  z-index: 3;
  margin: auto;
  // margin-bottom: -50px; 
  justify-content: space-between;
}
nav a{
  height: 70px;
  text-decoration: none;
  line-height: 70px;
  color: #666666;
  padding: 0 13px;
  font-size:12px;
  font-weight: 500;
  cursor: pointer;
  font-family: Helvetica, sans-serif;
  letter-spacing: .4px;
}
`
const Left = styled.div`
display: flex;
div{
    cursor: pointer;
}
div>img{
height: 35px;
margin-top: 20px;
pointer-events: none;
margin-right: 15px;
}
div>a{
  line-height: 80px;
  font-size: 16px;
  color: #000000;
}
div>a:hover{

  color: #0a6cff;
  text-decoration: underline;
}
`;

const Right = styled.div`
display: flex;

div{
    display: flex;
}
div>img{
    height: 15px;
    margin-top: 19px;
    padding: 0 14px;
}
div>a{
  line-height: 80px;
  font-size: 16px;
