import styled from "styled-components";
import {useEffect, useState} from "react";
import axios from "axios";
import {Link} from "react-router-dom";
import {Redirect} from 'react-router'
import {CREATE_COMPANY_MUTATION} from "../queries_mutations";
import {useMutation} from "@apollo/client";
import {companies} from "../data";
//styled-components-------

const Main1 = styled.div`
position: sticky;
top: 0;
height: 80px;
background-color: white;
z-index: 100;
box-shadow: 0 .1px 5px #ccc;

nav{
  display: flex;
  max-width: 1158px;
  height: 80px;
  background-color: white;
  top: 0;
  z-index: 3;
  margin: auto;
  // margin-bottom: -50px; 
  justify-content: space-between;
}
nav a{
  height: 70px;
  text-decoration: none;
  line-height: 70px;
  color: #666666;
  padding: 0 13px;
  font-size:12px;
  font-weight: 500;
  cursor: pointer;
  font-family: Helvetica, sans-serif;
  letter-spacing: .4px;
}
`
const Leftt = styled.div`
  display: flex;
  div{
      cursor: pointer;
  }
  div>img{
  height: 35px;
  margin-top: 20px;
  pointer-events: none;
  margin-right: 15px;
  }
  div>a{
  line-height: 80px;
  font-size: 16px;
  color: #000000;
}
  div>a:hover{

    color: #0a6cff;
    text-decoration: underline;
  }
`;

const Rightt = styled.div`
display: flex;

&>div{
    display: flex;
}
&>div>img{
    height: 15px;
    margin-top: 19px;
    padding: 0 14px;
}
div>a{
  line-height: 80px;
  font-size: 16px;
  color: #000000;
}
&>div>div>a{
    color: #141414;
    font-size: 16px;
}
&>div>div>a>button{

  width: 100px;
  height: 30px;
  margin-top: 20px;
  margin-left: 13px;
  font-size: 18px;
  font-weight: 600;
  color: white;
  border-radius: 5px;
  border: 1px solid #0a6cff;
  background-color: #0a6cff;
  cursor: pointer;
  &:hover{

    color: #0a6cff;
    border: 1px solid #0a6cff;
    background-color: white;
  }
}


div>div>a:hover{

    color: #0a6cff;
    text-decoration: underline;
}
`
/* Recruiter header css end */

const Wrapper = styled.div`
  *{padding: 0;
  margin: 0;
  box-sizing: border-box;}
  &>div{
    max-width: 617.5px;
    height: 100%;
    margin: auto;
    min-height: 250px;
  }
  &>div>div{
